"{MODULES_PATH}ecom/templates/admin/dspProdForm.tpl.htm","replace","<h3>Options</h3>","<h3>Tab Panels</h3>\n\t\t\t<label>Enabled Tabbed Description?</label>\n\t\t\t<input type=\"checkbox\" id=\"tpcb\" name=\"tabbedpanels\" value=\"1\" />\n\t\t\t<script type=\"text/javascript\">\n\t\t\t\tvar idcount={TPL_VAR_ID_COUNT};\n\t\t\t\t$(\"#tpcb\").change(function() {\n\t\t\t\t\tif(document.getElementById('tpcb').checked==true)\n\t\t\t\t\t{\n\t\t\t\t\t\t$('.tabbed').show();\n\t\t\t\t\t}\n\t\t\t\t\telse\n\t\t\t\t\t{\n\t\t\t\t\t\t$('.tabbed').hide();\n\t\t\t\t\t}\n\t\t\t\t});\n\t\t\t\tfunction addafter(e)\n\t\t\t\t{\n\t\t\t\t\tidcount = idcount + 1;\n\t\t\t\t\t$(e).parent().parent().after(\"<tr id='\"+idcount+\"'><td><input name='tabtitle[\"+idcount+\"]' type='text' class='formField' value=''/></td><td><textarea id='tabdesc\"+idcount+\"' name='tabdesc[\"+idcount+\"]'></textarea><sc\"+\"ript type='text/javascript'>CKEDITOR.replace('tabdesc\"+idcount+\"');</sc\"+\"ript></td><td><button type='button' class='formButton' onclick='addafter(this);'>Add Tab After</button><button type='button' class='formButton' onclick='byetab(this);'>Remove This Tab</button></td></tr>\");\n\t\t\t\t}\n\t\t\t\tfunction addatend()\n\t\t\t\t{\n\t\t\t\t\tidcount = idcount + 1;\n\t\t\t\t\t$('#tabtable > tbody').append(\"<tr id='\"+idcount+\"'><td><input name='tabtitle[\"+idcount+\"]' type='text' class='formField' value=''/></td><td><textarea id='tabdesc\"+idcount+\"' name='tabdesc[\"+idcount+\"]'></textarea></td><td><button type='button' class='formButton' onclick='addafter(this);'>Add Tab After</button><button type='button' class='formButton' onclick='byetab(this);'>Remove This Tab</button></td></tr>\");\n\t\t\t\t\tCKEDITOR.replace('tabdesc'+idcount);\n\t\t\t\t}\n\t\t\t\tfunction byetab(target)\n\t\t\t\t{\n\t\t\t\t\t$(target).parent().parent().remove();\n\t\t\t\t}\n\t\t\t\t$(document).ready(function()\n\t\t\t\t{\n\t\t\t\t\tif({TPL_VAR_TABBED_FLAG} === 1)\n\t\t\t\t\t{\n\t\t\t\t\t\tdocument.getElementById('